CREATE TABLE IF NOT EXISTS `engine4_sitecredit_modulelists` (
                     `modulelist_id` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
                      `name` varchar(128) NOT NULL,
                      `label` varchar(128) NOT NULL,
                      `parent_id` int(11) NOT NULL DEFAULT '0',
                      `order_id` int(11) NOT NULL DEFAULT '1',
                      `enabled` int(11) NOT NULL DEFAULT '1',
                      `is_submenu` int(11) NOT NULL DEFAULT '0'
                    ) ENGINE=InnoDB DEFAULT CHARSET=latin1 ROW_FORMAT=COMPACT;


CREATE TABLE IF NOT EXISTS `engine4_sitecredit_labels` (
                        `default_id` int(11) NOT NULL PRIMARY KEY AUTO_INCREMENT,
                        `type` varchar(128) NOT NULL,
                        `label` varchar(128) NOT NULL
                        ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
